import React from 'react'
import Popup from 'reactjs-popup'
import axios from 'axios'
import { Link } from 'react-router-dom'

import SimpleMap from '../../map/SimpleMap'
import Content from './content/Content'

// Utilities (date formatting, etc.)
import Util from '../../../components/misc/Util.js'

import classNames from 'classnames'
import styles from './details.module.scss'

// If DEMO_DATE exists, use it (frames all data in site relative to the demo
// date that is specified). Otherwise, today's date will be used ("now").
var DEMO_DATE = process.env.DEMO_DATE
if (typeof DEMO_DATE === 'undefined') {
  DEMO_DATE = '2025-07-04T23:56:00'
}
const now = DEMO_DATE !== undefined ? new Date(DEMO_DATE) : new Date();

// import { facility } from '../../../types/index'
const API_BASE = process.env.REACT_APP_API_BASE_URL;

// FC for Details.
const Details = (props: any) => {

  // Manage loading state (don't show if loading, etc.)
  const [loading, setLoading] = React.useState(true)

  // Get data for current facility.
  const facility = props.selectedFacility;

  // Submitted details, used in first table.
  const [detailsUpdates, setDetails] = React.useState();

  // Alert history, used in second table.
  const [alertHistory, setAlerts] = React.useState();

  // Status history, used to determine datetime stamp for facility status.
  const [statusHistory, setStatusHistory] = React.useState([])

  // Function to make API calls to get data for the four state variables above.
  const getDetailsData = async () => {
    // Make API calls to get state variable data from the appropriate endpoints.
    const res1 = await axios(
      `${API_BASE}/facilities/${props.facility_id}/alerts/history`
    )
    const res2 = await axios(
      `${API_BASE}/facilities/${props.facility_id}/details`
    )
    const alertsUrl = DEMO_DATE !== undefined ?
      `${API_BASE}/facilities/${props.facility_id}/alerts?date=${DEMO_DATE}` :
      `${API_BASE}/facilities/${props.facility_id}/alerts`;
    const res3 = await axios(alertsUrl);
    const res4 = await axios(
      `${API_BASE}/facilities/${props.facility_id}/status/history`
    );

    // Update the state variables with the response data.
    setAlerts(res1.data.data);
    setDetails(res2.data.data);
    const sortedAlerts = res3.data.data.sort(Util.sortByAlertId).sort(Util.sortByDatetime);
    setStatusHistory(res4.data.data);

    // Set state to loaded so JSX renders.
    setLoading(false);
  }

  // Effect hook to load API data.
  React.useEffect(() => {
    getDetailsData();
  }, [])

  // If loading do not show JSX content.
  if (loading) return (<div></div>);
  else {
    // Get datetime stamp for facility status and other elements
    const timeThreshold = DEMO_DATE !== undefined ? new Date(DEMO_DATE) : new Date();

    // Ignore details and alerts that are after the demo date or today's date;
    // do not show them in tables.
    const details = detailsUpdates.sort(Util.sortByDetailsId).sort(Util.sortByDatetime)
      .filter((d: any) => {
        const statusDate = new Date(d.effective_dtm);
        if (timeThreshold < statusDate) return false;
        return true
      });
    const alerts = alertHistory.sort(Util.sortByAlertId).sort(Util.sortByDatetime)
    .filter((d: any) => {
      const statusDate = new Date(d.effective_dtm);
      if (timeThreshold < statusDate) return false;
      return true
    });

    // Return JSX for details.
    return (<div className={styles.details}>
      <div className={classNames(styles.mapTitle, styles[facility.severity])}>
        <p className={styles.facilityName}>{facility.name}</p>
        <div className={classNames(styles.location, styles[facility.severity])}>
          <p>{facility.address}</p>
          <p>{facility.city}, {facility.state} {facility.zip}</p>
          <p>{facility.telephone}</p>
        </div>
      </div>
      <div className={classNames(styles.detailsTitle, styles[facility.severity])}>
        <div>
          <div>
            <p>
              <span>Facility status</span>
            </p>
            <p>
              <span>{Util.formatDate(now)}</span>
            </p>
          </div>
        </div>

        <Link to={'/submit/update/' + facility.facility_id}>
          <button>
            Update status
            <i className='material-icons'>edit</i>
          </button>
        </Link>

      </div>
      <SimpleMap selectedFacility={facility}/>
      <Content
        alertHistory={alertHistory}
        detailsUpdates={detailsUpdates}
        selectedFacility={facility}/>
    </div>);
  }
};

export default Details
