import React from 'react'
import styles from './definealerts.module.scss'
import classNames from 'classnames'
import Util from '../../../../components/misc/Util.js'
import AlertSelection from './alertSelection/AlertSelection.js';

// JSX for Define Alerts section of Submit page.
const DefineAlerts = ({
    stepNum,
    action,
    findInputs,
    setFindInputs,
    facilities,
    alertSelections,
    defineAlertsComplete,
    defineAlertsInputs,
    setDefineAlertsInputs,
  }) => {

  // Define Alerts component JSX.
  if (findInputs.facility.facility_id === undefined) return <div />
  else {
    const facilityName = Util.toTitleCase(findInputs.facility.name);
    const actionTitle = action === 'report' ? 'Report' : 'Update';
    return (
      <div className={styles.defineAlerts}>
        {(stepNum > 1) && <div className={styles.divider} />}
        <h2 className={styles.title}>{stepNum}. {actionTitle} alerts for {facilityName}</h2>
        <span className={styles.instructions}>Make a choice for each applicable category.</span>
        <div className={styles.alertSelections}>
          {
            alertSelections.map(a => {
              return (
                <AlertSelection
                def={a}
                defineAlertsInputs={defineAlertsInputs}
                setDefineAlertsInputs={setDefineAlertsInputs}
                allDefs={alertSelections}
                />
              );
            })
          }
        </div>
      {!defineAlertsComplete && <span className={styles.instructions}>Please complete all required selections to continue.</span>}
      </div>
    )
  }
}

export default DefineAlerts
