import React from 'react'
import classNames from 'classnames'
import dialysisIcon from '../../../assets/images/markers/dialysis-blue.png'
import hospitalIcon from '../../../assets/images/markers/hospital-blue.png'
import nursingIcon from '../../../assets/images/markers/nursing-blue.png'
import styles from './legend.module.scss'

const Legend = () => {
  const [open, setOpen] = React.useState(false)

  return (
    <div
      className={classNames(styles.mapOverlay, styles.legend, {
        [styles.open]: open
      })}
    >
      <div className={styles.toggleButton} onClick={() => setOpen(!open)}>
        <p>Legend</p>
        <i
          className={classNames('material-icons-outlined', {
            [styles.open]: open
          })}
        >
          expand_more
        </i>
      </div>
      <div className={styles.empty} />
      <div className={styles.contentContainer}>
        <p>Facility status alerts</p>
        {['Critical', 'Alerted', 'Stable'].map(label => (
          <div>
            <div
              className={classNames(styles.circle, styles[label.toLowerCase()])}
            />
            <p>{label} Status</p>
          </div>
        ))}
      </div>
      <div className={styles.contentContainer}>
        <p>Facility types</p>
        {[
          ['Hospital', hospitalIcon],
          ['Large dialysis center', dialysisIcon],
          ['Skilled nursing home', nursingIcon]
        ].map(([label, icon]) => (
          <div>
            <img src={icon} alt={`${label} marker`} />
            <p>{label}</p>
          </div>
        ))}
      </div>
    </div>
  )
}
export default Legend
