const initMap = (map, fillObservations, bubbleObservations) => {

  map.on('load', function() {
    initGeoms(fillObservations, bubbleObservations)
  })

  const initGeoms = (fillObservations, bubbleObservations) => {
    if (!map.getSource('geoms'))
      map.addSource('geoms', {
        type: 'vector',
        url: 'mapbox://traethethird.4kh7sxxt'
      })

    if (!map.getSource('centroids'))
      map.addSource('centroids', {
        type: 'vector',
        url: 'mapbox://traethethird.5u7sntcb'
      })

    fillObservations.forEach(( observation) => {
      const value = observation['value'];
      const place_id = observation['place_id']
      if (place_id === 16) {
        console.log(observation)
      }

      map.setFeatureState({source: 'geoms', sourceLayer: 'countries_id_rpr', id: place_id }, {clicked: false});
      if (!value) {
        map.setFeatureState({source: 'geoms', sourceLayer: 'countries_id_rpr', id: place_id }, {value: 0});
      } else {
        //const state = { value: Math.floor(256 * value)};
        const state = { value: value / 100};
        map.setFeatureState({source: 'geoms', sourceLayer: 'countries_id_rpr', id: place_id }, state);
      }
    });

    console.log(map.getStyle().layers)

    map.addLayer({
      id: 'geom-fills',
      type: 'fill',
      source: 'geoms',
      'source-layer': 'countries_id_rpr',
      paint: {
        'fill-color': [
          'step',
          ["feature-state", "value"],
              '#d3d3d3',
              0, '#d6fob2',
              0.35, '#b9d7a8',
              0.5, '#7fcdbb',
              0.65, '#41b6c4',
              0.8, '#2c7fb8',
              0.95, '#303d91'
        ],
        'fill-opacity': 0.9,
      }
    }, "country-small");

    map.addLayer({
      id: 'geom-line',
      type: 'line',
      source: 'geoms',
      'source-layer': 'countries_id_rpr',
      paint: {
        'line-color': [
          'case',
          ['boolean', ['feature-state', 'clicked'], true], '#ffffff',
          '#d3d3d3'
        ],
        'line-width': [
          'case',
            ['boolean', ['feature-state', 'clicked'], true], 5,
            1
          ],
      }
    }, "country-small");


    map.queryRenderedFeatures({layers: ['countries_id_rpr']}).forEach(( feature) => {
        console.log(feature)
    });

    bubbleObservations.forEach(( observation) => {
      const value = observation['value'];
      const place_id = observation['place_id']

      if (!value) {
        map.setFeatureState({source: 'centroids', sourceLayer: 'country_centroids-buzq3b', id: place_id }, {value: 0});
      } else {
        //const state = { value: Math.floor(256 * value)};
        const state = { value: value / 10};
        map.setFeatureState({source: 'centroids', sourceLayer: 'country_centroids-buzq3b', id: place_id }, state);
      }
    });

    map.addLayer({
      'id': 'population',
      'type': 'circle',
      'source': 'centroids',
      'source-layer': 'country_centroids-buzq3b',
      'paint': {
        'circle-radius': ["feature-state", "value"],
        'circle-color': '#ff0000',
        'circle-opacity': 0.4,
        'circle-stroke-width': 1,
        'circle-stroke-color': '#ffffff'
      }
    }, "country-small");
  }
}

export default initMap
