import React from 'react'
import { Bar, defaults } from 'react-chartjs-2'

const green = 'rgba(0, 154, 101, 1)' //'#009a65'
const yellow = 'rgba(249, 193, 59, 1)' //'#f9c13b'
const red = 'rgba(235, 28, 37, 1)' //'#eb1c25'

const options = {
  scales: {
    xAxes: [
      {
        stacked: true,
        gridLines: {
          display: false
        },
        scaleLabel: {
          display: true,
          labelString: 'State'
        }
      }
    ],
    yAxes: [
      {
        stacked: true,
        scaleLabel: {
          display: true,
          labelString: 'Alerts'
        },
        ticks: {
          beginAtZero: true,
          precision: 0,
          suggestedMax: 4,
        },
      }
    ]
  }
}

const Chart = ({ metric }) => {

  defaults.global.defaultFontFamily = 'Open Sans'

  const data = {
    labels: metric.map(observation => observation.date),
    datasets: metric.map(observation => observation.value)
  }

  return <Bar data={data} options={options} />
}

export default Chart
