import React from 'react'
import Popup from 'reactjs-popup'
import {Link} from 'react-router-dom'
import LandingMap from '../../map/LandingMap'

import reportIcon from '../../../assets/images/landingActions/report.png'
import viewIcon from '../../../assets/images/landingActions/view.png'
import updateIcon from '../../../assets/images/landingActions/report.png'

import styles from './landing.module.scss'

const Landing = ({ facilities, showSuccessModal, ...props }) => {
  const signupHtml = {
    preLink: `Individuals interested in learning more about the Power PIONEER Dashboard are encouraged to contact Project Director Eric Cote at `,
    postLink: `, or by calling 202-810-0125. Please also contact us to sign up for email updates about the Power PIONEER Dashboard.`,
  };
  const renderActions = [
    ['Report', reportIcon, "/submit/report"],
    ['View', viewIcon, "/alerts"],
    ['Update', updateIcon, "/submit/update"]
  ].map(([action, img, routePath]) => (
    <div>
      <div>
        <img src={img} />
      </div>
      <div>
        <p>{action} emergency power threats</p>
      </div>
      <div>
        {routePath && <Link to={routePath}><button>{action}</button></Link>}
        {!routePath &&

          <Popup
            trigger={open => (
              <button>{action}</button>
            )}
            position="top center"
            on="click"
            closeOnDocumentClick
          >
            <div>
              <span>Feature currently being developed.</span>
            </div>
          </Popup>

          }
      </div>
      <div className={"signupLink"}>
      <Popup
        trigger={open => (
          <a>Signup to {action.toLowerCase()}</a>
        )}
        position="top center"
        on="click"
        closeOnDocumentClick
      >
          <div>
          {signupHtml.preLink}
          <a target="_blank" href="mailto:cote@poweredforpatients.org">cote@poweredforpatients.org</a>
          {signupHtml.postLink}
        </div>
      </Popup>
      </div>
    </div>
  ))



  return (
    <div className={styles.landing}>
      <LandingMap facilities={facilities} />
      <div className={styles.actions}>{renderActions}</div>
      <div className={styles.warning}>
        <div>
          <p>Power PIONEER is NOT a substitute for 911.</p>
          <p>Always call 911 when a patient's life is in danger!</p>
        </div>
      </div>
      <Popup

        // Trigger button
        trigger={
          <button className={styles.submit}>
            Submit
          </button>
        }

        // Class name for styling (found in index.scss).
        className={'mapModal'}

        // Don't open the modal if it's already been shown.
        defaultOpen={showSuccessModal}

        // This is a modal.
        modal

        // When popup is closed, update state variable in App so it doesn't
        // get shown again this session.
        onClose={
          () => {

          }
        }
      >
        {
          close => (
          <div className={styles.modal}>
            <a className={styles.close} onClick={close}>
              &times;
            </a>
            <div className={styles.header}>Success</div>
            <div className={styles.content}>
              {" "}
              Your data submission was successful.
              {" "}
            </div>
            <div className={styles.modalActions}>
              <button
                className={styles.button}
                onClick={() => {
                  close();
                }}
              >
                OK
              </button>
            </div>
          </div>
        )
      }
      </Popup>
    </div>
  )
}

export default Landing
