import countriesSource from '../../assets/featureCollections/countries.geojson'

import dialysisIcon from '../../assets/images/markers/dialysis-blue.png'
import hospitalIcon from '../../assets/images/markers/hospital-blue.png'
import nursingIcon from '../../assets/images/markers/nursing-blue.png'
import dialysisStableIcon from '../../assets/images/markers/dialysis-green.png'
import hospitalStableIcon from '../../assets/images/markers/hospital-green.png'
import nursingStableIcon from '../../assets/images/markers/nursing-green.png'
import dialysisAlertedIcon from '../../assets/images/markers/dialysis-yellow.png'
import hospitalAlertedIcon from '../../assets/images/markers/hospital-yellow.png'
import nursingAlertedIcon from '../../assets/images/markers/nursing-yellow.png'
import dialysisCriticalIcon from '../../assets/images/markers/dialysis-red.png'
import hospitalCriticalIcon from '../../assets/images/markers/hospital-red.png'
import nursingCriticalIcon from '../../assets/images/markers/nursing-red.png'

const param = {
  facility: {
    minZoom: 8,
  },
  cluster: {
    maxZoom: 8,
    threshZoom: 7,
    // threshZoom: 14,
  }
};

const initMap = (map, fillObservations, bubbleObservations) => {

  map.on('load', function() {
    addImages()
    initGeoms(fillObservations, bubbleObservations)
  })

  const addImages = () => {
    Array(
      ['dialysis-stable', dialysisStableIcon],
      ['hospital-stable', hospitalStableIcon],
      ['nursing-stable', nursingStableIcon],
      ['dialysis-alerted', dialysisAlertedIcon],
      ['hospital-alerted', hospitalAlertedIcon],
      ['nursing-alerted', nursingAlertedIcon],
      ['dialysis-critical', dialysisCriticalIcon],
      ['hospital-critical', hospitalCriticalIcon],
      ['nursing-critical', nursingCriticalIcon]
    ).forEach(([name, icon]) => {
      map.loadImage(icon, (err, img) => {
        map.addImage(name, img)
      })
    })
  }

  const initGeoms = (fillObservations, bubbleObservations) => {
    if (!map.getSource('geoms'))
      map.addSource('geoms', {
        type: 'vector',
        url: 'mapbox://traethethird.36drkw1p'
      })

    console.log(fillObservations);
    console.log(bubbleObservations);

    fillObservations.forEach(( observation) => {
      const value = observation['value'];
      const place_id = observation['place_id']
      if (!value) {
        map.setFeatureState({source: 'geoms', sourceLayer: 'countries-9d1a76', id: place_id }, {value: 0});
      } else {
        const state = { value: Math.floor((256/1000) * value)};
        map.setFeatureState({source: 'geoms', sourceLayer: 'countries-9d1a76', id: place_id }, state);
      }
    });

    map.addLayer({
      id: 'geom-fills',
      type: 'fill',
      source: 'geoms',
      'source-layer': 'countries-9d1a76',
      paint: {
        'fill-color': [
          "rgb",
          // red is higher when feature.properties.temperature is higher
          ["feature-state", "value"],
          // green is always zero
          0,
          // blue is higher when feature.properties.temperature is lower
          ["-", 255, ["feature-state", "value"]]
        ],
        'fill-opacity': 0.5
      }
    })

    //map.addLayer({
    //  id: 'selected-fill',
    //  type: 'fill',
    //  source: 'geoms',
    //  filter: ['!', true],
    //  paint: {
    //    'fill-color': '#627BC1',
    //    'fill-opacity': 0.5
    //  }
    //})

    //map.addLayer({
    //  id: 'selected-border',
    //  type: 'line',
    //  source: 'geoms',
    //  filter: ['!', true],
    //  paint: {
    //    'line-color': '#627BC1',
    //    'line-width': 2
    //  }
    //})
  }
}

export default initMap
