import React from 'react'
import { Route, Switch, BrowserRouter } from 'react-router-dom'
import axios from 'axios'

// layout
import Alert from './components/layout/alert/Alert'
import Logo from './components/layout/logo/Logo'
import Header from './components/layout/header/Header'
import Footer from './components/layout/footer/Footer'

// views
import Alerts from './components/views/alerts/Alerts.js'
import Details from './components/views/details/Details'

// styles
import styles from './App.module.scss'
import 'material-design-icons/iconfont/material-icons.css'

const API_BASE = process.env.REACT_APP_API_BASE_URL
if (typeof API_BASE === 'undefined') {
  API_BASE = 'http://localhost:5002/'
}

var DEMO_DATE = process.env.DEMO_DATE
if (typeof DEMO_DATE === 'undefined') {
  DEMO_DATE = '2025-07-04T23:56:00'
}

console.log('DEMO_DATE')
console.log(DEMO_DATE)

//: React.FC
const App = () => {
  console.log('Render App')

  // Show the watermark on all pages? Controlled by checkbox in left side of
  // the header (gray area).
  const [showWm, setShowWm] = React.useState(false)

  // Load facility data.
  const [observations, setObservations] = React.useState([]);

  // Hide the "How to use this map" modal if it has already been displayed
  // once to the user.
  const [shownMapModal, setShownMapModal] = React.useState(false);

  // Track whether the component is still loading.
  const [loading, setLoading] = React.useState(true)

  // Unused state hooks below.
  // const [statusUpdates, setStatusUpdates] = React.useState([])
  // const [alertHistory, setAlertHistory] = React.useState([])
  // const [stableFacilities, setStableFacilities] = React.useState([])
  // const [alertedFacilities, setAlertedFacilities] = React.useState([])
  // const [criticalFacilities, setCriticalFacilities] = React.useState([])

  /**
   * Get observation data from API. Updates the observation data and loading status
   * when complete.
   * @method getObservations
   */
  async function getObservations() {
    const res = await axios(`${API_BASE}/observations`, {
      params: {
        metric_12: 2,
        temporal_resolution: 'yearly',
        spatial_resolution: 'country',
        start: '2018-01-01'
      }
    })

    setObservations(res.data.data)
    setLoading(false)
  }

  // When components are renders, get the facility data.
  React.useEffect(() => {
    getFacilities()
  }, [])

  // Functions to render each page's elements.
  const renderAlerts = loading ? <div /> :
    <Alerts // map page
      facilities={facilities} // facility data for map
      shownMapModal={shownMapModal} // don't show help modal more than once
      setShownMapModal={setShownMapModal} // update modal display status
      />

  const renderDetails = id => {
    if (loading) return <div />
    else {
      // if no selected facility, load the correct one based on the ID
      // if selected facility is in facilities data, set it
      const matchingFacility = facilities.find(f => +f.facility_id === +id)
      // if not in facilities data, load it directly
      // TODO

      return <Details facility_id={id} selectedFacility={matchingFacility} />
    }
  }

  // JSX for main app. Switch component allows links in the header to be used to
  // determine main app content.
  return (
    <div className={styles.app}>
      <BrowserRouter>
        <Alert />
        <Logo />
        <Switch>
          <Route
            path='/details/:id'
            render={d => {
              return (
                <Header showBack={true} showWm={showWm} setShowWm={setShowWm} />
              )
            }}
          />
          <Route
            render={d => {
              return (
                <Header
                  showBack={false}
                  showWm={showWm}
                  setShowWm={setShowWm}
                />
              )
            }}
          />
        </Switch>
        {showWm && <div className={styles.watermark} />}
        <Switch>
          <div>
            <Route exact path='/alerts' render={() => renderAlerts} />
            <Route exact path='/' component={() => renderAlerts} />
            <Route
              path='/details/:id'
              render={d => {
                return renderDetails(d.match.params.id)
              }}
            />
          </div>
        </Switch>
        <Footer />
      </BrowserRouter>
    </div>
  )
}

export default App
