import React from 'react'
import Popup from 'reactjs-popup'
import Alerts from '../../alerts/Alerts.js'

import styles from './statePopup.module.scss'

// : React.FC
const StatePopup = ({ state, facilities }) => {
  // console.log(state)
  // console.log(facilities)

  // split facilities by severity
  let stableCount = 0
  let alertedCount = 0
  let criticalCount = 0
  facilities.forEach(e => {
    switch (e.severity) {
      case 'stable':
        stableCount++
        break
      case 'alerted':
        alertedCount++
        break
      case 'critical':
        criticalCount++
        break
      default:
        break
    }
  })

  return (
    <div className={styles.container}>
      <div className={styles.header}>
        <div className={styles.titleContainer}>
          <p>
            {state}

            <Popup
              trigger={open => (
                <i className='material-icons-outlined'>info</i>
              )}
              position="top center"
              on="hover"
              closeOnDocumentClick
            >
              <div className={"infoTooltip"}>
                <span>Includes all currently active alerts for {state}.</span>
              </div>
            </Popup>

          </p>
          <p>
            {new Date().toLocaleString('en-us', {
              month: 'long',
              year: 'numeric',
              day: 'numeric'
            })}
          </p>
        </div>
      </div>
      <Alerts
        stableCount={stableCount}
        alertedCount={alertedCount}
        criticalCount={criticalCount}
      />
    </div>
  )
}

export default StatePopup
